use std::fmt::Display;

use anyhow::Result;
use serde::{Deserialize, Serialize};
use serde::export::Formatter;

/// Creates `ContractAbi` from given Json
pub fn create_contract_abi(contract_json: &str) -> Result<ContractAbi> {
    Ok(serde_json::from_str::<ContractAbi>(contract_json)?)
}

/// Contract JSON interface
#[derive(Serialize, Deserialize, Debug)]
#[serde(transparent)]
pub struct ContractAbi {
    pub functions: Vec<ContractFunction>
}

#[derive(Serialize, Deserialize, Debug)]
#[serde(rename_all = "camelCase")]
pub struct ContractFunction {
    #[serde(default)]
    pub inputs: Vec<InOutType>,
    #[serde(default)]
    pub name: String,
    #[serde(default)]
    pub outputs: Vec<InOutType>,
    #[serde(default)]
    pub state_mutability: StateMutability,
    pub r#type: FunctionType,
}

#[derive(Serialize, Deserialize, Debug, PartialEq)]
#[serde(rename_all = "lowercase")]
pub enum StateMutability {
    NONPAYABLE,
    PAYABLE,
    VIEW,
    PURE,
    NONE,
}

impl Default for StateMutability {
    fn default() -> Self {
        StateMutability::NONE
    }
}

#[derive(Serialize, Deserialize, Debug, PartialEq)]
#[serde(rename_all = "lowercase")]
pub enum ValueType {
    // value types
    UINT8,
    UINT16,
    UINT32,
    UINT64,
    UINT128,
    UINT160,
    UINT256,

    INT8,
    INT16,
    INT32,
    INT64,
    INT128,
    INT256,

    BOOL,
    ADDRESS,
    // 20 bytes
    BYTES1,// -BYTES32
    // BYTES4,// -BYTES32
    // BYTES32,

    // TUPLE,

    // Dynamically-sized byte array
    BYTES,
    STRING,
}

#[derive(Serialize, Deserialize, Debug, PartialEq)]
#[serde(rename_all = "lowercase")]
pub enum FunctionType {
    FUNCTION,
    CONSTRUCTOR,
    EVENT,
    // receive ether function
    RECEIVE,
    // 'default' function
    FALLBACK,
}

#[derive(Serialize, Deserialize, Debug)]
#[serde(rename_all = "camelCase")]
pub struct InOutType {
    pub name: String,
    pub r#type: ValueType,
}

pub trait HasName where Self: Display {
    fn name(&self) -> String {
        self.to_string().to_ascii_lowercase()
    }
}

impl Display for FunctionType {
    fn fmt(&self, f: &mut Formatter<'_>) -> std::fmt::Result {
        write!(f, "{:?}", self)
    }
}

impl Display for ValueType {
    fn fmt(&self, f: &mut Formatter<'_>) -> std::fmt::Result {
        write!(f, "{:?}", self)
    }
}

impl HasName for ValueType {}

impl HasName for FunctionType {}

